import React,{Component} from 'react'
import CardList from './CardList';
import {robots} from './robots';
import SearchBox from './SearchBox'

const state = {
    robots:robots,
    searchField:''
} 


class App extends Component { 
    constructor(){
        super();
        this.state = {
            robots: robots,
            searchField:''
        } 

    }
    render(){
        return ( 
            <div className='tc'>
            <h1>Robofriends</h1>
            <SearchBox/>
            <div className='pa2'>
            <hr/>
            </div>
            <CardList robots = {robots}/>
            </div>
        );
    }

}

export default App;
